                $optionLetter = 'a';

                while ($opt = mysqli_fetch_array($result1)) {
                    echo "<input type='radio' id='' value='" . $opt['opt'] . "' name='ans" . $qus_id . "[]'/>" . $optionLetter . ") " . $opt['opt'] . "  ";
